import React, { useState } from 'react';
import axios from 'axios';
import { BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, Legend } from 'recharts';
import './index.css';
 function App() {
  const [histogramData, setHistogramData] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [isSubmitted, setIsSubmitted] = useState(false);

   const fetchData = async () => {
    setSubmit(true);
    const response = await axios.get('https://www.terriblytinytales.com/test.txt');
    const text = response.data;
    const words = text.split(/\s+/);
    const frequencyMap = {};
    words.forEach((word) => {
      if (frequencyMap[word]) {
        frequencyMap[word]++;
      } else {
        frequencyMap[word] = 1;
      }
    });
    const sortedFrequency = Object.entries(frequencyMap).sort((a, b) => b[1] - a[1]);
    const top20Words = sortedFrequency.slice(0, 20);
    const histogramData = top20Words.map(([word, frequency]) => ({ word, frequency }));
    setHistogramData(histogramData);
    setIsLoading(false);
    setIsSubmitted(true);
  };
   const exportData = () => {
    const csvData = histogramData.map(({ word, frequency }) => `${word},${frequency}`).join('\n');
    const blob = new Blob([csvData], { type: 'text/csv;charset=utf-8;' });
    const url = URL.createObjectURL(blob);
    const link = document.createElement('a');
    link.href = url;
    link.setAttribute('download', 'histogram.csv');
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
  };
   return (
    <div>
    {!isSubmitted && 
      ( <button className='button' onClick={fetchData} disabled={isLoading}>
   Submit
  </button>
  )}

      {histogramData.length > 0 && (
       <div className='chart1'>
       <div>
          <BarChart width={1000} height={400} data={histogramData}>
            <CartesianGrid strokeDasharray="3 3" />
            <XAxis dataKey="word" />
            <YAxis />
            <Tooltip />
            <Legend />
            <Bar dataKey="frequency" fill="#8884d8" />
          </BarChart>
          <button onClick={exportData}>Export</button>
        </div></div>

      )}
    </div>
  );
}
 export default App;
